create table pembayaranuangmakan(
ID_Uang_Makan int not null,
NISN int not null,
Nama_Siswa varchar(50) not null,
Id_Kelas int not null,
Kelas varchar(10) not null,
Jurusan varchar(10) not null,
alergi varchar(20) not null default 'Tidak ada',
Jumlah_Pembayaran varchar(30) not null,
Tanggal_Pembayaran varchar(30) not null,
NIP_Admin int not null,
Nama_Admin varchar(30) not null,
primary key (ID_Uang_Makan)
);


select*from pembayaranuangmakan;